package jp.shuhei.core;import java.io.File;/** * ChainIntentInterface * @author Shuhei.iwamoto * @version 1.0.0 */public interface IFChainIntent<T extends IFChainIntent<T>> {    /**     * name     * @param className     * @return self     */    T name(Class<?> className);    /**     * moveActivity     * @return self     */    T moveActivity();    /**     * finish     * @return self     */    T finish();    /**     * overridePending     * @param enterAnim     * @param exitAnim     * @return     */    T overridePending(int enterAnim, int exitAnim);    /**     * actionViewVideo     * @param file     * @return self     */    T actionViewVideo(File file);    /**     * actionViewAudio     * @param file     * @return self     */    T actionViewAudio(File file);    /**     * actionSpecificPackage     * @param uri     * @param packageName     * @return     */    T actionSpecificPackage(String uri, String packageName);}