package jp.shuhei.core;import java.util.Set;/** * ChainPreferenceInterface * @author Shuhei.iwamoto * @version 1.0.0 */public interface IFChainPreference<T extends IFChainPreference<T>> {    /**     * save     * @param key     * @param value     * @return self     */    T save(String key, String value);    /**     * save     * @param key     * @param value     * @return self     */    T save(String key, int value);    /**     * save     * @param key     * @param value     * @return self     */    T save(String key, boolean value);    /**     * save     * @param key     * @param value     * @return self     */    T save(String key, float value);    /**     * save     * @param key     * @param value     * @return self     */    T save(String key, long value);    /**     * save     * @param key     * @param value     * @return self     */    T save(String key, Set<String> value);    /**     * getInt     * @param key     * @return self     */    int getInt(String key);    /**     * getString     * @param key     * @return self     */    String getString(String key);    /**     * getBoolean     * @param key     * @return self     */    boolean getBoolean(String key);    /**     * getFloat     * @param key     * @return self     */    float getFloat(String key);    /**     * getLong     * @param key     * @return self     */    long getLong(String key);    /**     * getSet     * @param key     * @return self     */    Set<String> getSet(String key);}